var gulp = require('gulp');
var typedoc = require("gulp-typedoc");

gulp.task("build", function () {
    return gulp
        .src(["model"])
        .pipe(typedoc({
            // TypeScript options (see typescript docs)
            module: "commonjs",
            target: "es5",
            includeDeclarations: true,
            out: "typedoc",
            name: "Atomist",
            // theme: "/path/to/theme",
            ignoreCompilerErrors: false,
            version: true,
        }));
});